mapper.insert:
insert into comment (comment_id, content_id, text, username,level,user_id) values (#{commentId}, #{contentId}, #{text},#{username},#{level},#{userId})
select date_Created,parent_id from comment where parent_id=LAST_INSERT_ID();
-- all fields : content_id, title, text, date_created, view_count, like_count, unlike_count

mapper.select:
select * from comment
where content_id=#{contentId}
  and comment_id is null
order by parent_id desc
    limit 1;

select a.parent_id as parent_id, a.comment_id as comment_id, a.level as level, a.content_id as content_id, a.text as text, a.date_created as date_created, a.username as username, a.user_id as user_id,
b.parent_id as bParentId, b.comment_id as bCommentId, b.level as bLevel, b.content_id as bContentId, b.text as bText, b.date_created as bDateCreated, b.username as bUsername, b.user_id as bUser_id
from comment a left join comment b ON a.parent_id=b.comment_id where a.level = 0 and a.content_id=#{contentId}
