mapper.update:
update content
set title=#{textUpdateDto.title},
    text=#{textUpdateDto.text}
where content_id=#{id}

mapper.select:
select content_id, title, text, date_created, view_count, like_count, unlike_count
from content
where content_id=#{id}

mapper.update:
update content
set view_count=view_count+1
where content_id=#{id}

update content
set like_count=like_count+1
where content_id=#{id}
